var GUI = require('GUI');
var logger = require('logger');
var Server = require('Server');
module.exports = GUItoServer = {
	exec: function(sID, code){
		try{
			logger.debug('emitiendo a ' + sID + ' exec' );

			Server.getSocket(sID).emit('exec', {
				code: code
			});
			
			logger.debug('Emitido a' + sID );
		}catch(e){
			logger.error(e);
		}
	},
	openRecord: function(sID, key, argNum, path){
		try{
			Server.getSocket(sID).emit('openRecord', {
				key: key,
				argNum: argNum,
				path: path || ''
			});
		}catch(e){
			logger.error(e);
		}
	}
};

GUI.exports('GUItoServer', GUItoServer);