var CLIArgs = require('CLIArgs'),
	express = require('express'),
	http = require('http'),
	path = require('path'),
	app = express()
;

CLIArgs.add('testserver', 'Carga un servidor de pruebas en el puerto 80 o en el definido en --testserver_port');
CLIArgs.add('testserver_port', 'Puerto en el que se cargara el servidor de pruebas', true, 80);

CLIArgs.on('processed', function(){
	if(CLIArgs.has('testserver')){
		app.configure(function(){
			app.set('port', CLIArgs.has('testserver_port') || 80);
			app.use(express.compress());
			app.use(express.favicon());
			app.use(express.logger('dev'));
			app.use(express.bodyParser());
			app.use(express.methodOverride());
			app.use(express.cookieParser('sdfg978s9dfg8s9df99g98s'));
			app.use(express.session());
			app.use(express['static'](path.join(__dirname, 'test_server')));
		});

		app.configure('development', function(){
			app.use(express.errorHandler());
		});

		//routes.init(app);

			//app.get('/', routes.index);
		//app.get('/users', user.list);

		var server = http.createServer(app);

		server.listen(app.get('port'), function(){
			console.log("Express server listening on port " + app.get('port'));
		});
	}
});

